/////////////////////////////////////////////////////////////////////////////
// CWzdView drawing

void CWzdView::OnDraw(CDC* pDC)
{
	CWzdDoc* pDoc = GetDocument();
	ASSERT_VALID(pDoc);

	// get bitmap colors
	CPalette *pOldPal = pDC->SelectPalette(m_bitmap.GetPalette(),FALSE);
	pDC->RealizePalette();

	// get device context to select bitmap into
	CDC dcComp;
	dcComp.CreateCompatibleDC(pDC);
	dcComp.SelectObject(&m_bitmap);

	// draw bitmap
	pDC->BitBlt(0,0,m_bitmap.m_Width,m_bitmap.m_Height, &dcComp, 0,0,SRCCOPY);

	// reselect old palette
	pDC->SelectPalette(pOldPal,FALSE);
}

