void CWzdView::OnWzdCommandRange(UINT nID)
{
	switch (nID)
	{
		case ID_TEST_WZD1:
			break;
		case ID_TEST_WZD2:
			break;		
		case ID_TEST_WZD3:
			break;
		case ID_TEST_WZD4:
			break;
	}
}

void CWzdView::OnUpdateWzdCommandRange(CCmdUI *pCmdUI)
{
	switch (pCmdUI->m_nID)
	{
		case ID_TEST_WZD1:
			break;
		case ID_TEST_WZD2:
			pCmdUI->SetRadio();
			break;		
		case ID_TEST_WZD3:
			break;
		case ID_TEST_WZD4:
			break;
	}
}
